function initSearch(){document.querySelector('[js-dom = "search-button"]').addEventListener("click",function(t){var n=document.querySelector('[js-dom = "search-condition"]').value.trim();window.location="/search?q="+n})}function initNav(){var n=document.querySelector("#mobile-menu-button"),e=document.querySelector("nav");n.addEventListener("click",function(t){n.classList.toggle("active"),e.classList.toggle("active")})}initSearch(),initNav();var form=document.querySelector('[js-dom = "member-form"]'),loginButton=document.querySelector('[js-dom = "login"]'),signupButton=document.querySelector('[js-dom = "signup"]');function initSubmitButton(n,t,e){t.addEventListener("click",function(t){n.method="post",n.action=e,n.submit()})}initSubmitButton(form,loginButton,"/session"),initSubmitButton(form,signupButton,"/user");