function initSearch(){document.querySelector('[js-dom = "search-button"]').addEventListener("click",function(e){var t=document.querySelector('[js-dom = "search-condition"]').value.trim();window.location="/search?q="+t})}function initNav(){var t=document.querySelector("#mobile-menu-button"),n=document.querySelector("nav");t.addEventListener("click",function(e){t.classList.toggle("active"),n.classList.toggle("active")})}initSearch(),initNav();